class HomeScreen extends StatefulWidget {
  const HomeScreen({super.key});
  @override
  State<HomeScreen> createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {
  int _selectedIndex = 0;
  final double _desktopBreakpoint = 768;

  void _onNavItemTapped(int index) {
    setState(() {
      _selectedIndex = index;
    });
  }

  @override
  Widget build(BuildContext context) {
    return LayoutBuilder(
      builder: (context, constraints) {
        if (constraints.maxWidth < _desktopBreakpoint) {
          return _buildMobileLayout();
        } else {
          return _buildDesktopLayout();
        }
      },
    );
  }

  Widget _buildDesktopLayout() {
    return Scaffold(
      body: NestedScrollView(
        headerSliverBuilder: (context, innerBoxIsScrolled) => [
          SliverAppBar(
            pinned: true,
            floating: true,
            title: _buildDesktopNavBar(),
            actions: [
              IconButton(icon: const Icon(Icons.search, size: 28), onPressed: () {}),
              const Padding(
                padding: EdgeInsets.symmetric(horizontal: 16.0),
                child: CircleAvatar(backgroundColor: Colors.deepPurple, child: Text('D')),
              ),
            ],
          ),
        ],
        body: IndexedStack(
          index: _selectedIndex,
          children: [
            _buildHomeContent(), // Llama a la función que construye el contenido de Home
            const PlaceholderScreen(title: 'Series'),
            const PlaceholderScreen(title: 'Movies'),
          ],
        ),
      ),
    );
  }

  Widget _buildMobileLayout() {
    return Scaffold(
      appBar: AppBar(
        leadingWidth: 80,
        leading: Padding(
          padding: const EdgeInsets.only(left: 16.0),
          child: _SmartImage(url: 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/ce/Max_logo.svg/2560px-Max_logo.svg.png', fit: BoxFit.contain),
        ),
        actions: [
          IconButton(icon: const Icon(Icons.search), onPressed: () {}),
          const Padding(
            padding: EdgeInsets.only(right: 16.0, left: 8.0),
            child: CircleAvatar(backgroundColor: Colors.deepPurple, radius: 16, child: Text('D')),
          ),
        ],
      ),
      body: IndexedStack(
        index: _selectedIndex,
        children: [
          _buildHomeContent(isMobile: true),
          const PlaceholderScreen(title: 'Series'),
          const PlaceholderScreen(title: 'Movies'),
        ],
      ),
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: _selectedIndex,
        onTap: _onNavItemTapped,
        items: const [
          BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Home'),
          BottomNavigationBarItem(icon: Icon(Icons.movie), label: 'Series'),
          BottomNavigationBarItem(icon: Icon(Icons.videocam), label: 'Movies'),
        ],
      ),
    );
  }
  
  Widget _buildHomeContent({bool isMobile = false}) {
    final List<Show> featuredShows = featuredData.map((data) => Show.fromMap(data)).toList();
    final List<Show> popularShows = popularData.map((data) => Show.fromMap(data)).toList();
    final List<Show> actionShows = actionData.map((data) => Show.fromMap(data)).toList();

    return ListView(
      padding: EdgeInsets.zero,
      children: [
        _FeaturedCarousel(shows: featuredShows, isMobile: isMobile),
        _ContentCarousel(title: 'Popular (Local)', shows: popularShows), // Título actualizado
        _ContentCarousel(title: 'Action Packed (URL)', shows: actionShows), // Título actualizado
        const SizedBox(height: 50),
      ],
    );
  }

  Widget _buildDesktopNavBar() {
    return Row(
      children: [
        _SmartImage(url: 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/ce/Max_logo.svg/2560px-Max_logo.svg.png', width: 60, fit: BoxFit.contain),
        const SizedBox(width: 40),
        _buildNavButton('Home', 0),
        const SizedBox(width: 20),
        _buildNavButton('Series', 1),
        const SizedBox(width: 20),
        _buildNavButton('Movies', 2),
      ],
    );
  }

  Widget _buildNavButton(String text, int index) {
    bool isSelected = _selectedIndex == index;
    return GestureDetector(
      onTap: () => _onNavItemTapped(index),
      child: Text(
        text,
        style: TextStyle(
          color: isSelected ? Colors.white : Colors.grey.shade400,
          fontSize: 16,
          fontWeight: isSelected ? FontWeight.bold : FontWeight.normal,
        ),
      ),
    );
  }
}

class _SmartImage extends StatelessWidget {
  final String url;
  final String? fallbackUrl;
  final BoxFit? fit;
  final double? height;
  final double? width;

  const _SmartImage({
    super.key,
    required this.url,
    this.fallbackUrl,
    this.fit = BoxFit.cover,
    this.height,
    this.width,
  });

  @override
  Widget build(BuildContext context) {
    if (url.isEmpty) {
      if (fallbackUrl != null && fallbackUrl!.isNotEmpty) {
        return _buildImage(fallbackUrl!);
      } else {
        return _buildErrorIcon();
      }
    }
    return _buildImage(url);
  }

  Widget _buildImage(String imageUrl) {
    final bool isNetwork = imageUrl.startsWith('http');
    final bool isLocalAsset = imageUrl.startsWith('assets/');

    if (isNetwork) {
      return Image.network(
        imageUrl,
        fit: fit,
        height: height,
        width: width,
        loadingBuilder: (context, child, progress) =>
            progress == null ? child : _buildLoading(),
        errorBuilder: (context, error, stackTrace) {
          if (fallbackUrl != null && fallbackUrl != imageUrl && fallbackUrl!.isNotEmpty) {
            return _buildImage(fallbackUrl!);
          }
          return _buildErrorIcon();
        },
      );
    } else if (isLocalAsset) {
      return Image.asset(
        imageUrl,
        fit: fit,
        height: height,
        width: width,
        errorBuilder: (context, error, stackTrace) {
          if (fallbackUrl != null && fallbackUrl != imageUrl && fallbackUrl!.isNotEmpty) {
            return _buildImage(fallbackUrl!);
          }
          return _buildErrorIcon(isLocal: true);
        },
      );
    } else {
      if (fallbackUrl != null && fallbackUrl!.isNotEmpty) {
        return _buildImage(fallbackUrl!);
      }
      return _buildErrorIcon();
    }
  }

  Widget _buildLoading() {
    return Container(
      height: height,
      width: width,
      color: Colors.grey.shade800,
      child: const Center(child: CircularProgressIndicator(color: Colors.white)),
    );
  }

  Widget _buildErrorIcon({bool isLocal = false}) {
    return Container(
      height: height,
      width: width,
      color: Colors.grey.shade800,
      child: Icon(
        isLocal ? Icons.broken_image_outlined : Icons.movie_outlined,
        color: Colors.white54,
        size: (height ?? width ?? 150) / 3,
      ),
    );
  }
}

class _FeaturedCarousel extends StatelessWidget {
  final List<Show> shows;
  final bool isMobile;
  const _FeaturedCarousel({super.key, required this.shows, this.isMobile = false});

  @override
  Widget build(BuildContext context) {
    return SizedBox(
      height: isMobile ? 350 : 500,
      child: PageView.builder(
        itemCount: shows.length,
        itemBuilder: (context, index) {
          final show = shows[index];
          return GestureDetector(
            onTap: () => Navigator.pushNamed(context, '/details', arguments: show),
            child: Stack(
              children: [
                Positioned.fill(
                  child: _SmartImage(url: show.backdropUrl, fit: BoxFit.cover),
                ),
                Positioned.fill(
                  child: Container(
                    decoration: BoxDecoration(
                      gradient: LinearGradient(
                        colors: [
                          const Color(0xFF141414).withOpacity(0.9),
                          Colors.transparent,
                          const Color(0xFF141414).withOpacity(0.9)
                        ],
                        begin: isMobile ? Alignment.topCenter : Alignment.centerLeft,
                        end: isMobile ? Alignment.bottomCenter : Alignment.centerRight,
                        stops: const [0, 0.5, 1],
                      ),
                    ),
                  ),
                ),
                Positioned(
                  left: isMobile ? 20 : 60,
                  bottom: isMobile ? 20 : 60,
                  right: isMobile ? 20 : null,
                  child: SizedBox(
                    width: isMobile ? null : 450,
                    child: Column(
                      crossAxisAlignment: isMobile ? CrossAxisAlignment.center : CrossAxisAlignment.start,
                      children: [
                        _SmartImage(
                          url: show.logoUrl,
                          height: isMobile ? 80 : 120,
                          fit: BoxFit.contain,
                        ),
                        const SizedBox(height: 20),
                        if (!isMobile)
                          Text(
                            show.description,
                            style: const TextStyle(color: Colors.white70, fontSize: 16, height: 1.4),
                            maxLines: 3,
                            overflow: TextOverflow.ellipsis,
                          ),
                      ],
                    ),
                  ),
                ),
              ],
            ),
          );
        },
      ),
    );
  }
}

class _ContentCarousel extends StatelessWidget {
  final String title;
  final List<Show> shows;
  const _ContentCarousel({super.key, required this.title, required this.shows});
  @override
  Widget build(BuildContext context) {
    final isMobile = MediaQuery.of(context).size.width < 768;
    return Padding(
      padding: const EdgeInsets.only(top: 30.0),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Padding(
            padding: EdgeInsets.symmetric(horizontal: isMobile ? 16.0 : 20.0),
            child: Text(title, style: const TextStyle(fontSize: 22, fontWeight: FontWeight.bold)),
          ),
          const SizedBox(height: 15),
          SizedBox(
            height: 220,
            child: ListView.builder(
              scrollDirection: Axis.horizontal,
              padding: EdgeInsets.symmetric(horizontal: isMobile ? 12.0 : 20.0),
              itemCount: shows.length,
              itemBuilder: (context, index) {
                final show = shows[index];
                return GestureDetector(
                  onTap: () => Navigator.pushNamed(context, '/details', arguments: show),
                  child: Card(
                    elevation: 4,
                    margin: const EdgeInsets.symmetric(horizontal: 8),
                    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                    clipBehavior: Clip.antiAlias,
                    child: SizedBox(
                      width: 150,
                      child: _SmartImage(
                        url: show.posterUrl,
                        fallbackUrl: show.backdropUrl,
                        fit: BoxFit.cover,
                      ),
                    ),
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}

